
public class OperEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		나머지연산자  %
		
		int share = 0;
		int remain = 0;
		
		share = 10 / 8;
//		/ = 몫
		
		remain = 10 % 8;
//		% = 나머지
		
		System.out.println("10을 8로나누면, ");
		System.out.println("몫은 " + share + "이고, 나머지는 " + remain + "입니다." );
				
	}

}
